[Form]
public class ICS_ExpenseVendInvoice extends FormRun implements SysSaveable
{
    private QueryBuildRange qbrExpenseApprovalStatus;
    private QueryBuildRange qbrBudgetApprovalStatus;
    private QueryBuildRange qbrJournalTablePosted;
    private QueryBuildRange qbrFlowTypeStatus;
    private QueryBuildRange qbrAmountStatus;

    private ICS_ExpenseVendInvoiceApprovalStatus    expenseApprovalStatusValue;
    private ICS_ExpenseVendInvoiceApprovalStatus    budgetApprovalStatusValue;
    private ICS_LedgerJournalTableStatusFilter      ledgerJournalTableStatusValue;
    private ICS_ExpenseFlowTypeStatusFilter         flowTypeStatusFilter;

    #Define.CurrentVersion(1)
    #LOCALMACRO.CurrentList
            expenseApprovalStatusValue,
            budgetApprovalStatusValue,
            ledgerJournalTableStatusValue
        #ENDMACRO

}

/// <summary>
/// Initializes a new instance of the form.
/// </summary>
