{
    super();

    Overview_OffsetLedgerDimension.parmIsDefaultAccount(true);
    Overview_OffsetLedgerDimension.parmValidateBlockedForManualEntry(true);
    InvoiceGroup_OffsetLedgerDimension.parmIsDefaultAccount(true);
    InvoiceGroup_OffsetLedgerDimension.parmValidateBlockedForManualEntry(true);

    element.initQueryRanges();

    ExpenseFlowTypeStatus.selection(ICS_ExpenseFlowTypeStatusFilter::NotRemoved);
}

