[Hookable(false)]
public container pack()
{
    return [#CurrentVersion, #CurrentList];
}

[Hookable(false)]
public boolean unpack(container packedClass)
{
    Integer version = conPeek(packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = packedClass;
            break;
        default :
            return false;
    }

    return true;
}

public void initParmDefault()
{
    ExpenseApprovalStatus.selection(expenseApprovalStatusValue);
    BudgetApprovalStatus.selection(budgetApprovalStatusValue);
    LedgerJournalTableStatus.selection(ledgerJournalTableStatusValue);
}

protected void initQueryRanges()
{
    QueryBuildDataSource    qbdsExpenseVendInvoice  = ICS_ExpenseVendInvoice_DS.queryBuildDataSource();
    QueryBuildDataSource    qbdsLedgerJournalTable  = LedgerJournalTable_DS.queryBuildDataSource();
    QueryBuildDataSource    qbdsVendInvoiceView     = ICS_ExpenseVendInvoiceAmountView_ds.queryBuildDataSource();

    qbrExpenseApprovalStatus    = SysQuery::findOrCreateRange(qbdsExpenseVendInvoice, fieldNum(ICS_ExpenseVendInvoice, ExpenseApproval));
    qbrBudgetApprovalStatus     = SysQuery::findOrCreateRange(qbdsExpenseVendInvoice, fieldNum(ICS_ExpenseVendInvoice, BudgetApproval));
    qbrFlowTypeStatus           = SysQuery::findOrCreateRange(qbdsExpenseVendInvoice, fieldNum(ICS_ExpenseVendInvoice, FlowType));
    qbrJournalTablePosted       = SysQuery::findOrCreateRange(qbdsLedgerJournalTable, fieldNum(LedgerJournalTable, Posted));  
    qbrAmountStatus             = SysQuery::findOrCreateRange(qbdsVendInvoiceView, fieldNum(ICS_ExpenseVendInvoiceAmountView, AmountOrig));
}

protected void joinLedgerTableDS()
{
    QueryBuildDataSource    qbdsLedgerJournalTable  = LedgerJournalTable_DS.queryBuildDataSource();

    switch (LedgerJournalTableStatus.selection())
    {
        case ICS_LedgerJournalTableStatusFilter::All:
            qbdsLedgerJournalTable.joinMode(JoinMode::OuterJoin);
            qbrJournalTablePosted.value(SysQuery::valueUnlimited());
            break;

        case ICS_LedgerJournalTableStatusFilter::NotCreated:
            qbdsLedgerJournalTable.joinMode(JoinMode::NoExistsJoin);
            qbrJournalTablePosted.value(SysQuery::valueUnlimited());
            break;

        case ICS_LedgerJournalTableStatusFilter::Created:
            qbdsLedgerJournalTable.joinMode(JoinMode::InnerJoin);
            qbrJournalTablePosted.value(SysQuery::value(NoYes::No));
            break;

        case ICS_LedgerJournalTableStatusFilter::Posted:
            qbdsLedgerJournalTable.joinMode(JoinMode::InnerJoin);
            qbrJournalTablePosted.value(SysQuery::value(NoYes::Yes));
            break;
    }
}

protected void clearOrUpdateRanges()
{
    if (ExpenseApprovalStatus.selection() == ICS_ExpenseVendInvoiceApprovalStatusFilter::All)
    {
        qbrExpenseApprovalStatus.value(SysQuery::valueUnlimited());
    }
    else
    {
        qbrExpenseApprovalStatus.value(ExpenseApprovalStatus.valueStr());
    }

    if (BudgetApprovalStatus.selection() == ICS_ExpenseVendInvoiceApprovalStatusFilter::All)
    {
        qbrBudgetApprovalStatus.value(SysQuery::valueUnlimited());
    }
    else
    {
        qbrBudgetApprovalStatus.value(BudgetApprovalStatus.valueStr());
    }

    switch (ExpenseFlowTypeStatus.selection())
    {
        case ICS_ExpenseFlowTypeStatusFilter::All:
            qbrFlowTypeStatus.value(SysQuery::valueUnlimited());
            break;
        case ICS_ExpenseFlowTypeStatusFilter::NotRemoved:
            qbrFlowTypeStatus.value(SysQuery::valueNot(ICS_ExpenseFlowType::Removed));
            break;
        case ICS_ExpenseFlowTypeStatusFilter::Removed:
            qbrFlowTypeStatus.value(queryValue(ICS_ExpenseFlowType::Removed));
            break;
        default:
            qbrFlowTypeStatus.value(ExpenseFlowTypeStatus.valueStr());
            break;
    }

    switch (ExpenseAmountStatus.selection())
    {
        case ICS_ExpenseAmountFilter::All:
            qbrAmountStatus.value(SysQuery::valueUnlimited());
            break;
        case ICS_ExpenseAmountFilter::NotCalculable:
            qbrAmountStatus.value(strFmt('((%1 == 0) || (%2 == 0))', 
                                        fieldStr(ICS_ExpenseVendInvoiceAmountView, AmountAI),
                                        fieldStr(ICS_ExpenseVendInvoiceAmountView, AmountOrig)));
            break;
        case ICS_ExpenseAmountFilter::Difference:
            qbrAmountStatus.value(strFmt('((%1 != 0) && (%2 != 0) && (%1 != %2))',
                                        fieldStr(ICS_ExpenseVendInvoiceAmountView, AmountAI),
                                        fieldStr(ICS_ExpenseVendInvoiceAmountView, AmountOrig)));
            break;
        case ICS_ExpenseAmountFilter::NoDifference:
            qbrAmountStatus.value(strFmt('((%1 != 0) && (%2 != 0) && (%1 == %2))',
                                        fieldStr(ICS_ExpenseVendInvoiceAmountView, AmountAI),
                                        fieldStr(ICS_ExpenseVendInvoiceAmountView, AmountOrig)));
            break;
        default:
            break;
    }
}

/// <summary>
/// Enables or disables the header buttons.
/// </summary>
public void enableHeaderButtons()
{
    boolean journalIsCreated    = ICS_ExpenseVendInvoice.LedgerJournalTableRecId != 0;
    boolean isDeleted           = ICS_ExpenseVendInvoice.FlowType == ICS_ExpenseFlowType::Removed;

    LedgerJournalGenerate.enabled(!journalIsCreated && !isDeleted);
    LedgerJournalPost.enabled(journalIsCreated && !LedgerJournalTable::findByRecId(ICS_ExpenseVendInvoice.LedgerJournalTableRecId).Posted);
    ShowInvoice.enabled(ICS_ExpenseVendInvoice.PathPreview != nullValueBaseType(Types::String));
    ExpenseApproval.enabled(!isDeleted);
    BudgetApproval.enabled(!isDeleted);
    ExpenseSync.enabled(!journalIsCreated);
}

/// <summary>
/// Active 
/// </summary>
/// <returns>Int</returns>
public int active()
{
    int ret;

    ret = super();

    allowEditFieldsOnFormDS_W(ICS_ExpenseVendInvoice_ds, ICS_ExpenseVendInvoice.LedgerJournalTableRecId == 0);

    ICS_ExpenseVendInvoice_ds.object(fieldNum(ICS_ExpenseVendInvoice, AIPreview)).allowEdit(true);
    ICS_ExpenseVendInvoice_ds.object(fieldNum(ICS_ExpenseVendInvoice, DirPathAI)).allowEdit(true);

    ICS_ExpenseVendInvoice_ds.object(fieldNum(ICS_ExpenseVendInvoice, MainContactWorker)).allowEdit(!ICS_ExpenseVendInvoiceLine::existSplittedLine(ICS_ExpenseVendInvoice.RecId)
                                                                                                  && ICS_ExpenseVendInvoice.LedgerJournalTableRecId == 0);

    element.enableHeaderButtons();

    return ret;
}

/// <summary>
/// Execute query
/// </summary>
public void executeQuery()
{ 
    int position;

    position = ICS_ExpenseVendInvoice_ds.getPosition();

    element.clearOrUPdateRanges();

    element.joinLedgerTableDS();

    super();

    ICS_ExpenseVendInvoice_ds.setPosition(position);
}

/// <summary>
/// Change color.
/// </summary>
/// <param name = "_record"></param>
/// <param name = "_options"></param>
public void displayOption(Common _record, FormRowDisplayOption _options)
{
    ICS_ExpenseVendInvoiceAmountView    vendInvoiceAmountView;
    ICS_ExpenseVendInvoice              vendInvoice = _record;       

    select firstonly vendInvoiceAmountView
        where vendInvoiceAmountView.ExpenseVendInvoice == vendInvoice.RecId;

    if (vendInvoiceAmountView.AmountAI      != 0
     && vendInvoiceAmountView.AmountOrig    != 0)
    {
        if (vendInvoiceAmountView.AmountAI == vendInvoiceAmountView.AmountOrig)
        {
            _options.backColor(WinAPI::RGB2int(144,238,144));
        }
        else
        {
            _options.backColor(WinAPI::RGB2int(255,254,122));
        }
    }

    super(_record, _options);
}

/// <summary>
/// Active
/// </summary>
/// <returns> Int</returns>
public int active()
{
    int ret;
    boolean journalTableExist = ICS_ExpenseVendInvoice.LedgerJournalTableRecId != 0;

    ret = super();

    ICS_ExpenseVendInvoiceLine_ds.object(fieldNum(ICS_ExpenseVendInvoiceLine, CurrencyCode)).allowEdit(ICS_ExpenseVendInvoiceLine.ParentLineRecId == 0);
    ICS_ExpenseVendInvoiceLine_ds.object(fieldNum(ICS_ExpenseVendInvoiceLine, AmountOrig)).allowEdit(ICS_ExpenseVendInvoiceLine.ParentLineRecId == 0);
    ICS_ExpenseVendInvoiceLineSplit.enabled(!journalTableExist);
    boolean allowDelete = !journalTableExist && !ICS_ExpenseVendInvoiceLine.displayIsParentLine();
    this.allowDelete(allowDelete);
    this.allowEdit(!journalTableExist);

    return ret;
}

